def preprocess_image(image_path):

    # Read the image

    image = cv2.imread(image_path)

   

    # Convert the image to grayscale

    gray_image = cv2.cvtColor(image, cv2.COLOR_BGR2GRAY)

   

    # Apply thresholding to create a binary image

    _, binary_image = cv2.threshold(gray_image, 128, 255, cv2.THRESH_BINARY_INV)

   

    # Perform morphological operations to clean the image

    kernel = np.ones((5, 5), np.uint8)

    cleaned_image = cv2.morphologyEx(binary_image, cv2.MORPH_CLOSE, kernel, iterations=2)

    return cleaned_image

 

def extract_letters(image):

    # Find contours in the image

    contours, _ = cv2.findContours(image, cv2.RETR_EXTERNAL, cv2.CHAIN_APPROX_SIMPLE)

   

    # Sort contours based on y-coordinate and x-coordinate

    contours = sorted(contours, key=lambda contour: (cv2.boundingRect(contour)[1], cv2.boundingRect(contour)[0]))

   

    letters = []

    for contour in contours:

        # Get bounding box of the contour

        x, y, w, h = cv2.boundingRect(contour)

       

        # Extract the letter from the original image

        letter = image[y:y+h, x:x+w]

       

        # Ensure the letter is large enough (adjust threshold based on your data)

        if w > 10 and h > 10:

            letters.append((letter, (x, y, w, h)))  # Store the letter along with its bounding box

   

    return letters

 

def classify_letters(letters):

    # Resize letters to match the input size expected by your CNN model

    resized_letters = [cv2.resize(letter[0], (28, 28)) for letter in letters]

   

    # Convert letters to a NumPy array

    input_data = np.array(resized_letters).reshape(-1, 28, 28, 1) / 255.0

   

    # Use the CNN model for classification

    predictions = model.predict(input_data)

   

    # Map predictions to characters using class_labels

    recognized_characters = [class_labels[np.argmax(prediction)] for prediction in predictions]

   

    return recognized_characters, [letter[1] for letter in letters]  # Return both recognized characters and bounding boxes

 

def reconstruct_text(predictions):

    # Concatenate the recognized characters to form the reconstructed text

    return ''.join(predictions)

 

def visualize_contours(image, bounding_boxes, output_path):

    # Draw bounding boxes on the image

    result_image = image.copy()

    for (x, y, w, h) in bounding_boxes:

        cv2.rectangle(result_image, (x, y), (x + w, y + h), (0, 255, 0), 2)  # Green rectangles

   

    # Save the image with bounding boxes

    cv2.imwrite(output_path, result_image)

 

def integrate_with_ocr(image_path, output_path):

    # Preprocess the image, extract letters, classify, and reconstruct the sequence

    cleaned_image = preprocess_image(image_path)

    letters_with_boxes = extract_letters(cleaned_image)

    predictions, bounding_boxes = classify_letters(letters_with_boxes)

    recognized_text = reconstruct_text(predictions)

 
    visualize_contours(cv2.imread(image_path), bounding_boxes, output_path)

   

    return recognized_text

